import { useState } from "react";
import { X } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertSubjectSchema } from "@shared/schema";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useLanguage } from "@/contexts/LanguageContext";

const formSchema = insertSubjectSchema.extend({
  hours: z.number().min(0).max(23),
  minutes: z.number().min(0).max(59),
});

type FormData = z.infer<typeof formSchema>;

interface AddSubjectModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (data: FormData) => Promise<void>;
}

export default function AddSubjectModal({ isOpen, onClose, onSubmit }: AddSubjectModalProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { t } = useLanguage();

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      hours: 2,
      minutes: 0,
    },
  });

  const handleSubmit = async (data: FormData) => {
    setIsSubmitting(true);
    try {
      await onSubmit(data);
      form.reset();
      onClose();
    } catch (error) {
      console.error("Error adding subject:", error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    form.reset();
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-md" data-testid="modal-add-subject">
        <DialogHeader>
          <DialogTitle data-testid="modal-title">{t('subjects.add')}</DialogTitle>
        </DialogHeader>

        <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4" data-testid="form-add-subject">
          <div>
            <Label htmlFor="name" className="block text-sm font-medium text-foreground mb-2">
              {t('subjects.name')}
            </Label>
            <Input
              id="name"
              type="text"
              placeholder={t('subjects.nameExample')}
              {...form.register("name")}
              className="w-full"
              data-testid="input-subject-name"
            />
            {form.formState.errors.name && (
              <p className="text-destructive text-sm mt-1" data-testid="error-subject-name">
                {form.formState.errors.name.message}
              </p>
            )}
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="hours" className="block text-sm font-medium text-foreground mb-2">
                {t('subjects.hours')}
              </Label>
              <Input
                id="hours"
                type="number"
                min="0"
                max="23"
                placeholder="2"
                {...form.register("hours", { valueAsNumber: true })}
                className="w-full"
                data-testid="input-subject-hours"
              />
              {form.formState.errors.hours && (
                <p className="text-destructive text-sm mt-1" data-testid="error-subject-hours">
                  {form.formState.errors.hours.message}
                </p>
              )}
            </div>

            <div>
              <Label htmlFor="minutes" className="block text-sm font-medium text-foreground mb-2">
                {t('subjects.minutes')}
              </Label>
              <Select 
                value={form.watch("minutes")?.toString() || "0"}
                onValueChange={(value) => form.setValue("minutes", parseInt(value))}
              >
                <SelectTrigger className="w-full" data-testid="select-subject-minutes">
                  <SelectValue placeholder="00" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="0">00</SelectItem>
                  <SelectItem value="15">15</SelectItem>
                  <SelectItem value="30">30</SelectItem>
                  <SelectItem value="45">45</SelectItem>
                </SelectContent>
              </Select>
              {form.formState.errors.minutes && (
                <p className="text-destructive text-sm mt-1" data-testid="error-subject-minutes">
                  {form.formState.errors.minutes.message}
                </p>
              )}
            </div>
          </div>

          <div className="flex space-x-3 pt-4">
            <Button 
              type="button" 
              variant="outline" 
              onClick={handleClose}
              className="flex-1"
              data-testid="button-cancel"
            >
              {t('modal.cancel')}
            </Button>
            <Button 
              type="submit" 
              disabled={isSubmitting}
              className="flex-1"
              data-testid="button-add-subject-submit"
            >
              {isSubmitting ? t('modal.adding') : t('modal.add')}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/dashboard-overview.content:
import { Plus, Calendar, Book, Clock, TrendingUp } from "lucide-react";
import { Subject, StudySettings } from "@shared/schema";
import { formatDuration } from "@/lib/schedule-generator";
import { useLanguage } from "@/contexts/LanguageContext";

interface DashboardOverviewProps {
  subjects: Subject[];
  settings: StudySettings;
  totalWeeks: number;
  onNewCycle: () => void;
  onCompleteCycle?: () => void;
}

export default function DashboardOverview({ 
  subjects, 
  settings, 
  totalWeeks, 
  onNewCycle,
  onCompleteCycle
}: DashboardOverviewProps) {
  const { t } = useLanguage();

  const totalStudyTime = subjects.reduce((total, subject) => {
    return total + (subject.hours * 60 + subject.minutes);
  }, 0);

  const totalStudyHours = Math.floor(totalStudyTime / 60);
  const totalStudyMinutes = totalStudyTime % 60;

  // Mock progress calculation
  const progress = subjects.length > 0 ? 67 : 0;

  return (
    <div className="mb-8">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-foreground mb-2" data-testid="title-current-cycle">
            {t('dashboard.currentCycle')}
          </h2>
          <p className="text-muted-foreground" data-testid="text-subtitle">
            {t('app.subtitle')}
          </p>
        </div>
        <div className="flex flex-col sm:flex-row gap-3 mt-4 md:mt-0">
          {onCompleteCycle && subjects.length > 0 && (
            <button 
              onClick={onCompleteCycle}
              className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition-colors font-medium flex items-center justify-center min-h-[40px]"
              data-testid="button-complete-cycle"
            >
              <span className="truncate">{t('dashboard.completeCycle')}</span>
            </button>
          )}
          <button 
            onClick={onNewCycle}
            className="bg-primary text-primary-foreground px-6 py-2 rounded-lg hover:bg-primary/90 transition-colors font-medium flex items-center justify-center min-h-[40px]"
            data-testid="button-new-cycle"
          >
            <Plus className="w-4 h-4 mr-2 flex-shrink-0" />
            <span className="truncate">{t('dashboard.newCycle')}</span>
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="bg-card p-6 rounded-xl border border-border shadow-sm" data-testid="card-total-weeks">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-muted-foreground text-sm font-medium">{t('dashboard.totalWeeks')}</p>
              <p className="text-2xl font-bold text-foreground mt-1" data-testid="text-total-weeks">
                {totalWeeks}
              </p>
            </div>
            <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center">
              <Calendar className="text-primary h-6 w-6" />
            </div>
          </div>
        </div>

        <div className="bg-card p-6 rounded-xl border border-border shadow-sm" data-testid="card-subjects">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-muted-foreground text-sm font-medium">{t('dashboard.subjects')}</p>
              <p className="text-2xl font-bold text-foreground mt-1" data-testid="text-subjects-count">
                {subjects.length}
              </p>
            </div>
            <div className="w-12 h-12 bg-secondary/10 rounded-lg flex items-center justify-center">
              <Book className="text-secondary h-6 w-6" />
            </div>
          </div>
        </div>

        <div className="bg-card p-6 rounded-xl border border-border shadow-sm" data-testid="card-daily-hours">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-muted-foreground text-sm font-medium">{t('dashboard.dailyHours')}</p>
              <p className="text-2xl font-bold text-foreground mt-1" data-testid="text-daily-hours">
                {formatDuration(settings.dailyStudyHours, settings.dailyStudyMinutes)}
              </p>
            </div>
            <div className="w-12 h-12 bg-accent rounded-lg flex items-center justify-center">
              <Clock className="text-accent-foreground h-6 w-6" />
            </div>
          </div>
        </div>

        <div className="bg-card p-6 rounded-xl border border-border shadow-sm" data-testid="card-progress">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-muted-foreground text-sm font-medium">{t('dashboard.progress')}</p>
              <p className="text-2xl font-bold text-foreground mt-1" data-testid="text-progress-percent">
                {progress}%
              </p>
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
              <TrendingUp className="text-green-600 h-6 w-6" />
            </div>
          </div>
